import React from "react";

export class Counter extends React.Component {
  
    state = {
        count : 0
    }

    constructor(props){
        super(props)

        setInterval(() => {
            this.setState({
                count : state.count+1
            })
        }, 1000);
    }
    render() {
    return <div> <h1>{this.state}</h1></div>;
  }
}
